--
-- Create procedure `sp_generate_new_code`
--
CREATE 
	DEFINER = 'root'@'localhost'
PROCEDURE sp_generate_new_code()
BEGIN
    DECLARE max_number INT;
    DECLARE new_code VARCHAR(100);
    DECLARE length_code INT;

-- Tìm mã có phần số lớn nhất
SELECT
  MAX(CAST(SUBSTRING(fixed_asset_code, 3) AS UNSIGNED)) INTO max_number
FROM `misa.fresher032023.qlts.nguyentienvu`.fixed_asset fa;
  
    -- Tính độ dài của chuỗi số mới
    IF LENGTH(max_number) = LENGTH(max_number + 1) THEN
        SET length_code = LENGTH(max_number);
    ELSE
        SET length_code = LENGTH(max_number) + 1;
    END IF;
    
    -- Tăng phần số lên 1 đơn vị và tạo mã mới
    SET new_code = CONCAT(
        SUBSTRING(
            ( SELECT
    fixed_asset_code
  FROM `misa.fresher032023.qlts.nguyentienvu`.fixed_asset
  ORDER BY fixed_asset_code DESC
  LIMIT 1),
1,
2
),
LPAD(max_number + 1, length_code, '0')
);

-- Trả về mã mới
SELECT
  new_code;
END
$$

