PROCEDURE sp_delete_fixed_assets(IN Ids VARCHAR(2000))
BEGIN
  DECLARE id CHAR(36);
  DECLARE commaPosition int;
  SET Ids = CONCAT(Ids, ',');
  
  -- tách lấy id và xóa
  WHILE LENGTH(Ids) > 0 DO
    SET commaPosition = LOCATE(',', Ids);
    SET id = TRIM(SUBSTRING(Ids, 1, commaPosition-1));
    SET Ids = SUBSTRING(Ids, commaPosition+1);

-- xóa tài sản theo id
