PROCEDURE sp_check_duplicate_fixed_asset_code(
    IN fixed_asset_id char(36),             -- id
    IN fixed_asset_code varchar(100)        -- mã
)
BEGIN
    IF EXISTS ( SELECT
    fixed_asset_code
  FROM fixed_asset fa
  WHERE fa.fixed_asset_code = fixed_asset_code) THEN
        -- mã đã tồn tại trong database, lấy ra id của mã đó và so sánh với mã truyền vào
        IF EXISTS ( SELECT
