  TRUE AS is_duplicate;
ELSE
-- id khác nhau, mã bị trùng, trả về false
SELECT
  FALSE AS is_duplicate;
END IF;
ELSE
-- mã chưa tồn tại trong database, trả về true
SELECT
  TRUE AS is_duplicate;
END IF;
END
$$

