--
-- Create procedure `sp_filtering_and_paging`
--
CREATE 
	DEFINER = 'root'@'localhost'
PROCEDURE sp_filtering_and_paging(
    IN pageNumber int,                  -- số trang muốn trả về
    IN pageSize int,                    -- số bản ghi mỗi trang
    IN FilterName VARCHAR(100),         -- tên tài sản cần lọc
    IN FilterDepartment VARCHAR(100),   -- tên phòng ban
    IN FilterCategory varchar(100)      -- tên loại tài sản cần lọc
)
BEGIN
    -- tính số bản ghỉ cần bỏ qua là lưu vào @offset
    SET @Offset = (pageNumber - 1) * pageSize;
    
    -- gán một câu truy vấn cho @Sql dựa trên diều kiện lọc
    SET @Sql = CONCAT('
        SELECT *
        FROM fixed_asset
        WHERE fixed_asset_name LIKE ?
            AND department_name LIKE ?
            AND fixed_asset_category_name LIKE ?
        ORDER BY created_date
        LIMIT ?, ?
    ');
    
    -- chuẩn bị truy vấn để thực hiên
    PREPARE stmt FROM @Sql;
    SET @FilterName = CONCAT('%', FilterName, '%');
    SET @FilterDepartment = CONCAT('%', FilterDepartment, '%');
    SET @FilterCategory = CONCAT('%', FilterCategory, '%');
    -- thực hiện lọc
    EXECUTE stmt USING @FilterName, @FilterDepartment, @FilterCategory, @Offset, pageSize;
    -- giải phóng tài nguyên đã chuẩn bị
    DEALLOCATE PREPARE stmt;
END
$$

